INSERT INTO LIVRO (ID, TITULO, DESCRICAO) VALUES (1, "A Batalha do Apocalipse", "Há muitos e muitos anos, tantos quanto o número de estrelas no céu, o paraíso celeste foi palco de um terrível levante. Um grupo de anjos guerreiros, amantes da justiça e da liberdade, desafiou a tirania dos poderosos arcanjos, levantando armas contra seus opressores ...");
INSERT INTO LIVRO (ID, TITULO, DESCRICAO) VALUES (2, "Filhos do Éden - Herdeiros de Atlântida - Livro 1", "Há uma guerra no céu. O confronto civil entre o arcanjo Miguel e as tropas revolucionárias de seu irmão, Gabriel, devasta as sete camadas do paraíso. Com as legiões divididas, as fortalezas sitiadas, os generais estabeleceram um armistício na terra, uma trégua frágil e delicada, que pode desmoronar a qualquer instante ...");
INSERT INTO LIVRO (ID, TITULO, DESCRICAO) VALUES (3, "Filhos do Éden - Anjos da Morte - Livro 2", "Desde eras longínquas, os malakins, anjos virtuosos e sábios, observam e estudam o progresso do homem. Mas eis que chega o século XX, e com ele a acelerada degradação do planeta ...");
INSERT INTO LIVRO (ID, TITULO, DESCRICAO) VALUES (4, "O Capital", "O Capital é um conjunto de livros de Karl Marx que constituem uma análise do capitalismo.");
